// App.js
import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';
import CandidateList from './components/CandidateList';
import CandidateForm from './CandidateForm';

const App = () => {
  const [candidates, setCandidates] = useState([]);

  const addCandidate = (candidate) => {
    setCandidates([...candidates, candidate]);
  };

  const updateCandidate = (id, updatedCandidate) => {
    const updatedList = candidates.map((c) => (c.id === id ? updatedCandidate : c));
    setCandidates(updatedList);
  };

  const removeCandidate = (id) => {
    const updatedList = candidates.filter((c) => c.id !== id);
    setCandidates(updatedList);
  };

  return (
    <Router>
      <Switch>
        <Route
          path="/"
          exact
          render={() => (
            <CandidateList candidates={candidates} removeCandidate={removeCandidate} />
          )}
        />
        <Route
          path="/add"
          render={() => <CandidateForm onSubmit={addCandidate} />}
        />
        <Route
          path="/update/:id"
          render={(props) => (
            <CandidateForm
              onSubmit={(updatedCandidate) =>
                updateCandidate(props.match.params.id, updatedCandidate)
              }
              candidate={candidates.find((c) => c.id === props.match.params.id)}
            />
          )}
        />
      </Switch>
    </Router>
  );
};

export default App;
