import React, { useState } from 'react';
import { Candidate } from '../types/CandidateType';
import { DragDropContext, Droppable } from 'react-beautiful-dnd';
import CandidateCard from './CandidateCard';
import styled from 'styled-components';
import { useNavigate } from 'react-router-dom';
import search from '../assets/images/search.png';

const CANDIDATE_STATE = [
  { id: 'contact_id', status: 'Contact'},
  { id: 'dialog_id', status: 'Dialogue'},
  { id: 'interview_id', status: 'Interview'},
  { id: 'closed_id', status: 'Closed'},
]

interface CandidateListProps {
  candidates: Candidate[];
  removeCandidate: (id: string) => void;
  handleDragDrop: (result: any) => void;
}

const CandidateList: React.FC<CandidateListProps> = ({ candidates, handleDragDrop, removeCandidate}) => {
  const navigate = useNavigate()

  const [searchTerm, setSearchTerm] = useState('');

  const filteredCandidates = candidates.filter((candidate) =>
    candidate.firstName?.toLowerCase().includes(searchTerm.toLowerCase())
  );

  const handleSearchChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setSearchTerm(e.target.value);
  };

  return (
    <>
      <h1>Candidate List</h1>
      <div style={{ display: 'flex', justifyContent: 'space-between' }}>
        <LinkWrapper onClick={() => navigate('/add')}>Add New Candidate</LinkWrapper>
        <LinkWrapper>
          <input
            style={{ border: 'none', height: 20 }}
            type="text"
            placeholder="Search by name"
            value={searchTerm}
            onChange={handleSearchChange}
          />
          <img src={search} alt='search' style={{height: 20, width: 20, paddingLeft: 5 }}/>
        </LinkWrapper>
      </div>


      <StatusColomn>
      {filteredCandidates.length <= 0 ? (<NoContent>Your search gives no results!</NoContent>):(
        <DragDropContext onDragEnd={(result) => handleDragDrop(result)}>
        {CANDIDATE_STATE.map((category) => (
          <Droppable droppableId={category.status} key={category.id} type='group'>
            {(provided) => (
              <ListItem {...provided.droppableProps} ref={provided.innerRef}>
              <Title {...provided.droppableProps}>{category.status}</Title>
              {filteredCandidates.length > 0 && (
                filteredCandidates.filter((candidate) => candidate.status === category.status)
                .map((candidate, index) => (
                  <CandidateCard candidate={candidate} removeCandidate={removeCandidate} index={index} key={index}/>
                ))
              )}
              {provided.placeholder}
            </ListItem>
            )}
          </Droppable>
        ))}
    </DragDropContext>
      )}
      </StatusColomn>
    </>
  )
}

const StatusColomn = styled.div`
  display: flex;
`
const ListItem = styled.div`
  overflow-anchor: none;
  background-color: #E5F3FF;
  display: flex;
  flex-direction: column;
  margin: 10px;
  border-radius: 16px;
  flex: 1;
  padding-bottom: 30px
  `
const Title = styled.h3`
  padding-bottom: 8px;
  border-bottom: 1px;
  text-align: center;
`
const LinkWrapper = styled.div`
  padding: 10px;
  border: 2px solid #E5F3FF;
  max-width: 300px;
  border-radius: 6px;
  background: #E5F3FF;
  font-weight: bold;
  display: flex;
`
const NoContent = styled.div`
  justify-content: center;
  font-size: 20px;
  color: crimson;
  font-weight: bold;
`

export default CandidateList
