import React from 'react';
import { Candidate } from '../types/CandidateType';
import { DragDropContext, Droppable } from 'react-beautiful-dnd';
import CandidateCard from './CandidateCard';

const CANDIDATE_STATE = [
  { id: 'contact_id', state: 'CONTACT'},
  { id: 'dialog_id', state: 'DIALOGUE'},
  { id: 'interview_id', state: 'INTERVIEW'},
  { id: 'closed_id', state: 'CLOSED'},
]

interface CandidateListProps {
  candidates: Candidate[];
  removeCandidate: (id: string) => void;
  handleDragDrop: (result: any) => void;
}

const CandidateList: React.FC<CandidateListProps> = ({ candidates, removeCandidate, handleDragDrop }) => {
  return (
    <>
      <h1>Candidate List</h1>
      <div style={{ display: 'flex', flexDirection: 'row', justifyContent: 'space-between'} }>
        <DragDropContext onDragEnd={(result) => handleDragDrop(result)}>
            {CANDIDATE_STATE.map((category) => (
              <Droppable droppableId={category.state} key={category.id}>
                {(provided) => (
                  <div {...provided.droppableProps} ref={provided.innerRef}>{category.state}
                    <div>
                      {candidates
                        .filter((candidate) => candidate.status.toUpperCase() === category.state)
                        .map((candidate, index) => (
                          <CandidateCard candidate={candidate} index={index} key={index}/>
                      ))}
                    </div>
                    {provided.placeholder}
                </div>
                )}
              </Droppable>
            ))}
        </DragDropContext>
      </div>
    </>
  )
}

export default CandidateList
