import { Candidate } from "../types/CandidateType";

export const ACTION = {
  ADD_CANDIDATE: 'addCandidate',
  UPDATE_CANDIDATE: 'updateCandidate',
  SEARCH_CANDIDATE: 'searchCandidate',
  REMOVE_CANDIDATE: 'removeCandidate'
}

type SearchAction =
{
  type: 'searchCandidate'| 'removeCandidate',
  payload: string
}
type AddAction =
{
  type: 'addCandidate' | 'updateCandidate',
  payload: Candidate
}
export type Action = SearchAction | AddAction

export const reducer = (candidates: Candidate[], action: Action): Candidate[]=> {
  switch(action.type){
    case 'addCandidate':
      return [...candidates, action.payload]
    case 'updateCandidate':
      return [...candidates, action.payload]
    case 'searchCandidate':
      return candidates.filter(candidate => candidate.id !== action.payload)
    case 'removeCandidate':
      return candidates.filter(candidate => candidate.id !== action.payload)

    default:
      return candidates
  }
}
