import React, { useReducer, useState } from 'react';
import { Routes, Route } from 'react-router-dom';
import CandidateList from './components/CandidateList';
import CandidateForm from './components/CandidateForm';
import { Candidate } from './types/CandidateType';
import { Candidates } from './data/CandidateData';
import { reducer } from './hooks/reducer';
import styled from 'styled-components';

const App: React.FC = () => {
  const [candidates, dispatch] = useReducer(reducer, Candidates);


  const addCandidate = (candidate: Candidate) => {
    dispatch({ type: 'addCandidate',  payload: candidate })
  };

  // const updateCandidate = (id: number, updatedCandidate: Candidate) => {
  //   const updatedList = candidates.map((c) => (c.id === id ? updatedCandidate : c));
  //   setCandidates(updatedList);
  // };

  const removeCandidate = (id: string) => {
    dispatch({ type: 'removeCandidate',  payload: id })
  };

  const handleDragDrop = (result: any) => {
    const { destination, source } = result
    console.log(result)
    const sourceIndex = source.index
    //const destinationIndex = destination.index

    if(!destination || destination.droppableId === source.droppableId) return

    if(destination.droppableId !== source.droppableId){
      const updateState = candidates[sourceIndex].status = destination.droppableId
      dispatch({ type: 'updateCandidate', payload: updateState })
    }
  }

  return (
    <AppContainer>
    <CandidateForm onSubmit={addCandidate} />
    <CandidateList candidates={candidates} removeCandidate={removeCandidate} handleDragDrop={handleDragDrop}/>
  </AppContainer>
  )
};

export default App;

const AppContainer = styled.div`
background-color: #f0f8ff
`
