import { Candidate } from "../types/CandidateType";

export const ACTION = {
  ADD_CANDIDATE: 'addCandidate',
  UPDATE_CANDIDATE: 'updateCandidate',
  SEARCH_CANDIDATE: 'searchCandidate',
  REMOVE_CANDIDATE: 'removeCandidate'
}

export type Action =
| {type: 'addCandidate' }
| {type: 'updateCandidate' }
| {type: 'searchCandidate' }
| {type: 'removeCandidate' }


const addCandidate = (candidate: Candidate) => {
   dispatch({type: ACTION.ADD_CANDIDATE, payload: {candidate: candidate}})
};

// const newTodo = (name) => {
//   return {id: Date.now(), name: name, complete: false}
// }

export const reducer = (state: any, action: Action) => {
  switch(action.type){
    case 'addCandidate':
      return [...state, action.payload.candidate]
    case ACTION.REMOVE_CANDIDATE:
      return todos.map(todo => {
        if(todo.id === action.payLoad.id) {
          return {...todo, complete: !todo.complete}
        } return todo
      })
    case ACTION.SEARCH_CANDIDATE:
      return todos.filter(todo => todo.id !== action.payload.id)
      case ACTION.UPDATE_CANDIDATE:
        return todos.filter(todo => todo.id !== action.payload.id)
    default:
      return state
  }
}
