            <CandidateForm
              onSubmit={(updatedCandidate) =>
                updateCandidate(props.match.params.id, updatedCandidate)
              }
              candidate={candidates.find((c) => c.id === props.match.params.id)}
            />
          )}
        /> */}
    </Routes>
  );
};

export default App;
