import React, { useState } from 'react';
import { Candidate } from '../types/CandidateType';
import { useNavigate } from 'react-router-dom';
import styled from 'styled-components';

const formFields = [
  { name: 'full Name', children: [
    { name: 'firstName', type: 'text' },
    { name: 'lastName', type: 'text' },
  ]},
  { name: 'age', type: 'number' },
  { name: 'current Address', children: [
    { name: 'street address', type: 'text' },
    { name: 'city', type: 'text' },
  ]},
  { name: 'email', type: 'email' },
  { name: 'status', type: 'text' },
]

interface CandidateFormProps {
  onSubmit: (candidate: Candidate) => void;
  candidate?: Candidate;
}

const CandidateForm: React.FC<CandidateFormProps> = ({ onSubmit, candidate }) => {
  const navigate = useNavigate()

  const [formData, setFormData] = useState<Candidate>(candidate || {
    id: '',
    firstName: '',
    lastName: '',
    age: 0,
    email: '',
    address: '',
    status: '',
  });

  const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    onSubmit(formData);
    setFormData({
        id: '',
        firstName: '',
        lastName: '',
        age: 0,
        email: '',
        address: '',
        status: '',
      })
    navigate("/")
  };

  return (
    <>
      <h1>Add Candidate</h1>
      <form onSubmit={handleSubmit}>
        {formFields.map((field) => (
          <RowContainer>
            <Title>{field.name}</Title>
            {}
            {field.children?.map((inner) =>
                        <OuterDiv>
                        <InputWrapper>
                          <Input
                            type={inner.type}
                            name={inner.name as keyof Candidate}
                            value={formData[field.name as keyof Candidate]} onChange={handleChange} />
                          <label>{inner.name}</label>
                          </InputWrapper>
                        </OuterDiv>
            )}
            <OuterDiv>
            <InputWrapper>
              <Input
                type={field.type}
                name={field.name as keyof Candidate}
                value={formData[field.name as keyof Candidate]} onChange={handleChange} />
              <label>{field.name}</label>
              </InputWrapper>
            </OuterDiv>
        </RowContainer>
        ))}
      <button type="submit">Submit</button>
    </form>
    </>
  );
};

const RowContainer = styled.div`
  display: flex;
  flex-direction: column;
  padding: 20px;
`
const Title = styled.div`
  font-weight: bold;
  font-size: 18px;
  padding-bottom: 6px;
`
const OuterDiv = styled.div`
  display: flex;
  flex: 1;
`
const InputWrapper = styled.div`
  display: flex;
  flex-direction: column;
  flex: 1;
  padding-right: 20px;
  margin: 6px;
`

const Input = styled.input`
  height: 50px;
  border-color: #b8bdc9;
  border-radius: 6px;
  color: #2c3345;
  margin-bottom: 6px;
`

export default CandidateForm;
