  //         {(provided) => (
  //           <div {...provided.droppableProps} ref={provided.innerRef}>
  //             <div>
  //               {newcandidates.map((candidate, index) => (
  //                 <Draggable draggableId={candidate.id} key={candidate.id} index={index}>
  //                   {(provided) => (
  //                     <div {...provided.dragHandleProps} {...provided.draggableProps} ref={provided.innerRef}>
  //                       {candidate.name} - {candidate.currentStage}
  //                       {/* <button onClick={() => removeCandidate(candidate.id)}>Remove</button> */}
  //                     </div>
  //                   )}

  //                 </Draggable>
  //               ))}
  //             </div>
  //             {provided.placeholder}
  //         </div>
  //         )}
  //         </Droppable>
  //       </div>

  //       <div style={{ background: 'blue'}}>
  //         <Droppable droppableId='DIALOGUE'>
  //         {(provided) => (
  //           <div {...provided.droppableProps} ref={provided.innerRef}>
  //           <div>
  //             {CANDIDATE_STATE.map((candidate, index) => (
  //               <Draggable draggableId={candidate.id} key={candidate.id} index={index}>
  //                 {(provided) => (
  //                   <div {...provided.dragHandleProps} {...provided.draggableProps} ref={provided.innerRef}>
  //                     {candidate.id} - {candidate.state}
  //                     {/* <button onClick={() => removeCandidate(candidate.id)}>Remove</button> */}
  //                   </div>
  //               )}

  //               </Draggable>
  //             ))}
  //           </div>
  //             {provided.placeholder}
  //         </div>
  //         )}
  //         </Droppable>
  //       </div>
  //       </div>
  //     </DragDropContext>
  //   </>
  // )
}

export default CandidateList
