import React, { useState } from 'react';
import { Candidate } from '../types/CandidateType';
import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';
import CandidateCard from './CandidateCard';

const CANDIDATE_STATE = [
  { id: 'contact_id', state: 'CONTACT'},
  { id: 'dialog_id', state: 'DIALOGUE'},
  { id: 'interview_id', state: 'INTERVIEW'},
  { id: 'closed_id', state: 'CLOSED'},
]

interface CandidateListProps {
  candidates: Candidate[];
  removeCandidate: (id: string) => void;
}

const CandidateList: React.FC<CandidateListProps> = ({ candidates, removeCandidate }) => {

  const [newcandidates, setNewcandidates] = useState(candidates)

  const handleDragDrop = (result: any) => {
    const { destination, source } = result
    console.log(result)
    const sourceIndex = source.index
    //const destinationIndex = destination.index

    if(!destination || destination.droppableId === source.droppableId) return

    if(destination.droppableId !== source.droppableId){
      const updateState = newcandidates[sourceIndex].status = destination.droppableId
      setNewcandidates([...newcandidates, updateState])
    }
  }

  return (
    <>
      <h1>Candidate List</h1>
      <div style={{ display: 'flex', flexDirection: 'row', justifyContent: 'space-between'} }>
        <DragDropContext onDragEnd={handleDragDrop}>
            {CANDIDATE_STATE.map((categori) => (
              <Droppable droppableId={categori.state} key={categori.id}>
                {(provided) => (
                  <div {...provided.droppableProps} ref={provided.innerRef}>{categori.state}
                    <div>
                      {newcandidates
                        .filter((candidate) => candidate.status === categori.state)
                        .map((candidate, index) => (
                          <CandidateCard candidate={candidate} index={index}/>
                      ))}
                    </div>
                    {provided.placeholder}
                </div>
                )}
              </Droppable>
            ))}
        </DragDropContext>
      </div>
    </>
  )
}

export default CandidateList
