import React, { useState } from 'react';
import { Candidate } from '../types/CandidateType';
import { useNavigate } from 'react-router-dom';
import styled from 'styled-components';

interface CandidateFormProps {
  onSubmit: (candidate: Candidate) => void;
  candidate?: Candidate;
}

const CandidateForm: React.FC<CandidateFormProps> = ({ onSubmit, candidate }) => {
  const navigate = useNavigate()

  const [formData, setFormData] = useState<Candidate>(candidate || {
    id: '',
    name: '',
    age: 0,
    email: '',
    address: '',
    status: '',
  });

  const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    onSubmit(formData);
    setFormData({
        id: '',
        name: '',
        age: 0,
        email: '',
        address: '',
        status: '',
      })
    navigate("/")
  };

  return (
    <>
      <h1>Add Candidate</h1>
      <form onSubmit={handleSubmit}>
      <div style={{ display: 'flex', flexDirection: 'column', padding: 20 }}>
        <div style={{ paddingBottom: 20 }}>Full Name</div>
        <div style={{ display: 'flex', flex: 1}}>
          <div>
            <input style={{ height: 30, borderColor: '#b8bdc9', borderRadius: 6, margin: 6, color: '#2c3345'}} type="text" name="firstName" value={formData.name} onChange={handleChange} />
            <label>First Name</label>
          </div>
          <div style={{ display: 'flex', flexDirection: 'column', flex: 1, paddingRight: 20 }}>
            <input type="text" name="lastName" value={formData.name} onChange={handleChange} />
            <label>Last Name</label>
          </div>
        </div>
      </div>

      <label>
        Age:
        <input type="number" name="age" value={formData.age} onChange={handleChange} />
      </label>
      <label>
        Email:
        <input type="email" name="email" value={formData.email} onChange={handleChange} />
      </label>
      <label>
        Address:
        <input type="text" name="address" value={formData.address} onChange={handleChange} />
      </label>
      <label>
        Status:
        <input type="text" name="status" value={formData.status} onChange={handleChange} />
      </label>
      <button type="submit">Submit</button>
    </form>
    </>
  );
};

const InputWrapper = styled.div`
  display: flex,
  flexDirection: column,
  flex: 1,
  paddingRight: 20px
  `
export default CandidateForm;
