import { Draggable } from "react-beautiful-dnd"
import { Candidate } from '../types/CandidateType';
import styled from 'styled-components'
import address from '../assets/images/location.svg'
import avatar from '../assets/images/avatar.svg'

interface Props {
  candidate: Candidate;
  index: number;
}

const CandidateCard: React.FC<Props> = ({ candidate, index }) => {
  return (
    <Draggable draggableId={candidate.id} key={candidate.id} index={index}>
      {(provided) => (
        <CardsContainer {...provided.dragHandleProps} {...provided.draggableProps} ref={provided.innerRef}>
          <IconWrapper>
            <img src={avatar} alt='avatar' style={{ paddingRight: '10px' }}/>
            <Name>{candidate.name}</Name>
          </IconWrapper>
          <div>Age: {candidate.age}</div>
          <IconWrapper>
            <img src={address} alt='address' />
            <div>{candidate.address}</div>
          </IconWrapper>
          <div>Status: {candidate.status}</div>
          {/* <button onClick={() => removeCandidate(candidate.id)}>Remove</button> */}
        </CardsContainer>
        )}
      </Draggable>
  )
}

export default CandidateCard

const CardsContainer = styled.div`
  padding: 10px;
  border-radius: 6px;
  background-color: #ffffff;
  margin: 5px;
`

const IconWrapper = styled.div`
  display: flex;
`

const Name = styled.div`
  font-size: 16px;
  font-weight: bold
`
