import React, { useState } from 'react';
import { Candidate } from '../types/CandidateType';
import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';

const CANDIDATE_STATE = [
  { id: 'contact_id', state: 'CONTACT'},
  { id: 'dialog_id', state: 'DIALOGUE'},
  { id: 'interview_id', state: 'INTERVIEW'},
  { id: 'closed_id', state: 'CLOSED'},
]

interface CandidateListProps {
  candidates: Candidate[];
  removeCandidate: (id: string) => void;
}

const CandidateList: React.FC<CandidateListProps> = ({ candidates, removeCandidate }) => {
  const [newcandidates, setNewcandidates] = useState(candidates)
  const handleDragDrop = (result: any) => {
    const { destination, source } = result
    const sourceIndex = source.index
    const destinationIndex = destination.index
    if(!destination || (destination.draggableId === source.draggableId && sourceIndex === destinationIndex)) return

    const reorderdItems = [...newcandidates]
    const [removedItem] = reorderdItems.splice(sourceIndex, 1)
    reorderdItems.splice(destinationIndex, 0, removedItem)
    return setNewcandidates(reorderdItems)
  }

  return (
    <>
      <h1>Candidate List</h1>
      <DragDropContext onDragEnd={handleDragDrop}>
        <div style={{ display: 'flex', flexDirection: 'row'} }>
        <div style={{ background: 'green'}}>
          <Droppable droppableId='droppable'>{(provided) => (
            <div {...provided.droppableProps} ref={provided.innerRef}>
              {CANDIDATE_STATE.map((candidate, index) => (
                <Droppable droppableId={candidate.state}>
                  {(provided) => (
                    <div {...provided.droppableProps} ref={provided.innerRef}>
                      <div>
                        {newcandidates.map((candidate, index) => (
                          <Draggable draggableId={candidate.id} key={candidate.id} index={index}>
                            {(provided) => (
                              <div {...provided.dragHandleProps} {...provided.draggableProps} ref={provided.innerRef}>
                                {candidate.name} - {candidate.currentStage}
                                {/* <button onClick={() => removeCandidate(candidate.id)}>Remove</button> */}
                              </div>
                            )}

                          </Draggable>
                        ))}
                      </div>
                      {provided.placeholder}
                    </div>
                  )}
                </Droppable>
              ))}
            </div>
          )}
          </Droppable>
        </div>
        </div>
      </DragDropContext>
    </>
  )
}

export default CandidateList
